import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useBookings } from '../context/BookingContext';

const BookingForm = () => {
  const navigate = useNavigate();
  const { createBooking, calculateFare } = useBookings();
  
  // Form state
  const [formData, setFormData] = useState({
    pickupLocation: '',
    dropLocation: '',
    date: '',
    time: '',
    cabType: ''
  });
  
  // Fare estimation state
  const [fareEstimate, setFareEstimate] = useState(null);
  const [showFareBreakdown, setShowFareBreakdown] = useState(false);

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
    
    // Clear fare estimate when form changes
    if (showFareBreakdown) {
      setShowFareBreakdown(false);
    }
  };

  // Estimate fare
  const handleEstimateFare = () => {
    const { pickupLocation, dropLocation, cabType } = formData;
    
    if (!pickupLocation || !dropLocation || !cabType) {
      alert('Please fill in pickup location, drop location, and select a cab type.');
      return;
    }
    
    const estimatedFare = calculateFare(cabType);
    setFareEstimate(estimatedFare);
    setShowFareBreakdown(true);
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const { pickupLocation, dropLocation, date, time, cabType } = formData;
    
    // Validate form
    if (!pickupLocation || !dropLocation || !date || !time || !cabType) {
      alert('Please fill in all required fields');
      return;
    }
    
    // If fare wasn't estimated, calculate it now
    let fare = fareEstimate;
    if (!fare) {
      fare = calculateFare(cabType);
      setFareEstimate(fare);
    }
    
    // Create booking object
    const bookingData = {
      ...formData,
      fare: parseFloat(fare.totalFare)
    };
    
    // Send booking to API
    const newBooking = await createBooking(bookingData);
    
    if (newBooking) {
      // Navigate to bookings page on success
      navigate('/bookings');
    }
  };

  return (
    <div className="py-10">
      <div className="container mx-auto px-4">
        <div className="max-w-4xl mx-auto">
          <div className="mb-10 text-center">
            <h1 className="text-3xl font-bold mb-2">Book Your Cab</h1>
            <p className="text-gray">Fill in the details to book your ride</p>
          </div>
          
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
            <div className="lg:col-span-2">
              <form onSubmit={handleSubmit} className="card">
                <div className="form-group">
                  <label htmlFor="pickupLocation" className="form-label">Pickup Location</label>
                  <input
                    type="text"
                    id="pickupLocation"
                    name="pickupLocation"
                    className="form-input"
                    placeholder="Enter pickup address"
                    value={formData.pickupLocation}
                    onChange={handleChange}
                    required
                  />
                </div>
                
                <div className="form-group">
                  <label htmlFor="dropLocation" className="form-label">Drop Location</label>
                  <input
                    type="text"
                    id="dropLocation"
                    name="dropLocation"
                    className="form-input"
                    placeholder="Enter destination address"
                    value={formData.dropLocation}
                    onChange={handleChange}
                    required
                  />
                </div>
                
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="form-group">
                    <label htmlFor="date" className="form-label">Date</label>
                    <input
                      type="date"
                      id="date"
                      name="date"
                      className="form-input"
                      value={formData.date}
                      onChange={handleChange}
                      min={new Date().toISOString().split('T')[0]} // Set min date to today
                      required
                    />
                  </div>
                  
                  <div className="form-group">
                    <label htmlFor="time" className="form-label">Time</label>
                    <input
                      type="time"
                      id="time"
                      name="time"
                      className="form-input"
                      value={formData.time}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
                
                <div className="form-group">
                  <label htmlFor="cabType" className="form-label">Cab Type</label>
                  <select
                    id="cabType"
                    name="cabType"
                    className="form-input"
                    value={formData.cabType}
                    onChange={handleChange}
                    required
                  >
                    <option value="" disabled>Select a cab type</option>
                    <option value="mini">Mini (4 seater)</option>
                    <option value="sedan">Sedan (4 seater)</option>
                    <option value="suv">SUV (6 seater)</option>
                  </select>
                </div>
                
                <div className="mt-8 flex flex-col sm:flex-row gap-4">
                  <button 
                    type="button" 
                    className="btn btn-secondary flex-1"
                    onClick={handleEstimateFare}
                  >
                    Estimate Fare
                  </button>
                  <button 
                    type="submit" 
                    className="btn btn-primary flex-1"
                  >
                    Book Now
                  </button>
                </div>
              </form>
            </div>
            
            <div className="card h-fit">
              <h3 className="text-xl font-bold mb-4">Fare Estimate</h3>
              {!showFareBreakdown ? (
                <p className="text-gray">
                  Select pickup and drop locations to see the estimated fare.
                </p>
              ) : (
                <div className="mt-4">
                  <div className="flex justify-between mb-2">
                    <span>Base Fare:</span>
                    <span>${fareEstimate.baseFare}</span>
                  </div>
                  <div className="flex justify-between mb-2">
                    <span>Distance Charge:</span>
                    <span>${fareEstimate.distanceCharge} ({fareEstimate.distance} miles)</span>
                  </div>
                  <div className="pt-4 mt-4 border-t flex justify-between font-bold">
                    <span>Estimated Total:</span>
                    <span>${fareEstimate.totalFare}</span>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BookingForm;
