import React, { useState } from 'react';
import { Link, NavLink } from 'react-router-dom';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  return (
    <header className="bg-white shadow sticky top-0 z-50">
      <nav className="container mx-auto px-4 py-4 flex justify-between items-center">
        <Link to="/" className="text-2xl font-bold text-primary">
          SwiftCab
        </Link>
        
        <div className="lg:hidden">
          <button 
            onClick={toggleMenu}
            className="text-dark hover:text-primary focus:outline-none"
            aria-label="Toggle menu"
          >
            <svg 
              className="w-6 h-6" 
              fill="none" 
              stroke="currentColor" 
              viewBox="0 0 24 24" 
              xmlns="http://www.w3.org/2000/svg"
            >
              {isMenuOpen ? (
                <path 
                  strokeLinecap="round" 
                  strokeLinejoin="round" 
                  strokeWidth={2} 
                  d="M6 18L18 6M6 6l12 12" 
                />
              ) : (
                <path 
                  strokeLinecap="round" 
                  strokeLinejoin="round" 
                  strokeWidth={2} 
                  d="M4 6h16M4 12h16M4 18h16" 
                />
              )}
            </svg>
          </button>
        </div>
        
        <ul className={`lg:flex lg:items-center lg:space-x-8 ${isMenuOpen ? 
          'absolute top-16 left-0 right-0 bg-white shadow-md flex flex-col p-4 space-y-4 z-50' : 
          'hidden lg:flex'}`}
        >
          <li>
            <NavLink 
              to="/" 
              className={({ isActive }) => 
                isActive 
                  ? "text-primary font-semibold" 
                  : "text-dark hover:text-primary font-semibold"
              }
              onClick={closeMenu}
            >
              Home
            </NavLink>
          </li>
          <li>
            <NavLink 
              to="/book" 
              className={({ isActive }) => 
                isActive 
                  ? "text-primary font-semibold" 
                  : "text-dark hover:text-primary font-semibold"
              }
              onClick={closeMenu}
            >
              Book a Cab
            </NavLink>
          </li>
          <li>
            <NavLink 
              to="/bookings" 
              className={({ isActive }) => 
                isActive 
                  ? "text-primary font-semibold" 
                  : "text-dark hover:text-primary font-semibold"
              }
              onClick={closeMenu}
            >
              My Bookings
            </NavLink>
          </li>
        </ul>
        
        <Link 
          to="/book" 
          className="hidden lg:block btn btn-primary"
        >
          Book Now
        </Link>
      </nav>
    </header>
  );
};

export default Header;
