import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useBookings } from '../context/BookingContext';

// Status badge component
const StatusBadge = ({ status }) => {
  let badgeClass = 'badge-';
  
  switch (status) {
    case 'pending':
      badgeClass += 'pending';
      break;
    case 'on the way':
      badgeClass += 'onway';
      break;
    case 'arrived':
      badgeClass += 'arrived';
      break;
    case 'completed':
      badgeClass += 'completed';
      break;
    case 'cancelled':
      badgeClass += 'cancelled';
      break;
    default:
      badgeClass += 'pending';
  }
  
  return <span className={`badge ${badgeClass}`}>{status}</span>;
};

// Format date helper function
const formatDateTime = (dateString) => {
  const date = new Date(dateString);
  return date.toLocaleString('en-US', {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    hour: 'numeric',
    minute: 'numeric',
    hour12: true
  });
};

const Bookings = () => {
  const { bookings, loading, error, fetchBookings, cancelBooking } = useBookings();
  
  useEffect(() => {
    // Refresh bookings when component mounts
    fetchBookings();
  }, [fetchBookings]);
  
  const handleCancelBooking = async (id) => {
    if (window.confirm('Are you sure you want to cancel this booking?')) {
      await cancelBooking(id);
    }
  };
  
  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-4 border-primary border-t-transparent"></div>
          <p className="mt-2 text-gray">Loading bookings...</p>
        </div>
      </div>
    );
  }
  
  if (error) {
    return (
      <div className="text-center py-10">
        <div className="text-error text-xl mb-4">
          <i className="fas fa-exclamation-circle mr-2"></i>
          Error loading bookings
        </div>
        <p className="text-gray mb-4">{error}</p>
        <button 
          onClick={fetchBookings} 
          className="btn btn-primary"
        >
          Try Again
        </button>
      </div>
    );
  }
  
  return (
    <div className="py-10">
      <div className="container mx-auto px-4">
        <div className="mb-10 flex flex-col md:flex-row md:items-center justify-between gap-4">
          <div>
            <h1 className="text-3xl font-bold mb-2">My Bookings</h1>
            <p className="text-gray">View and manage your bookings</p>
          </div>
          <Link to="/book" className="btn btn-primary self-start">
            Book a New Ride
          </Link>
        </div>
        
        {bookings.length === 0 ? (
          <div className="text-center py-16 bg-gray-light rounded-lg">
            <div className="text-5xl text-gray mb-4">
              <i className="fas fa-car"></i>
            </div>
            <h2 className="text-2xl font-bold mb-2">No bookings yet</h2>
            <p className="text-gray mb-6">You haven't made any bookings yet.</p>
            <Link to="/book" className="btn btn-primary">
              Book Your First Ride
            </Link>
          </div>
        ) : (
          <div className="grid grid-cols-1 gap-6">
            {bookings.map((booking) => (
              <div key={booking._id} className="card border border-gray-200 hover:shadow-md transition-shadow">
                <div className="flex flex-col md:flex-row">
                  <div className="flex-grow p-2">
                    <div className="flex flex-col md:flex-row md:items-center justify-between mb-4">
                      <div>
                        <h3 className="text-lg font-bold mb-1">
                          Booking #{booking.bookingId}
                        </h3>
                        <p className="text-sm text-gray">
                          {formatDateTime(booking.createdAt)}
                        </p>
                      </div>
                      <StatusBadge status={booking.status} />
                    </div>
                    
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      <div>
                        <div className="mb-3">
                          <div className="text-sm text-gray">Pickup Location</div>
                          <div className="font-medium">{booking.pickupLocation}</div>
                        </div>
                        <div>
                          <div className="text-sm text-gray">Drop Location</div>
                          <div className="font-medium">{booking.dropLocation}</div>
                        </div>
                      </div>
                      <div>
                        <div className="mb-3">
                          <div className="text-sm text-gray">Date & Time</div>
                          <div className="font-medium">
                            {new Date(booking.date).toLocaleDateString()} at {booking.time}
                          </div>
                        </div>
                        <div className="mb-3">
                          <div className="text-sm text-gray">Cab Type</div>
                          <div className="font-medium capitalize">{booking.cabType}</div>
                        </div>
                        <div>
                          <div className="text-sm text-gray">Fare</div>
                          <div className="font-medium">${booking.fare.toFixed(2)}</div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
                
                <div className="border-t p-4 flex flex-wrap gap-2 justify-end">
                  <Link 
                    to={`/bookings/${booking._id}`} 
                    className="px-4 py-2 bg-gray-100 text-dark rounded-md hover:bg-gray-200 transition-colors"
                  >
                    View Details
                  </Link>
                  
                  {booking.status !== 'cancelled' && booking.status !== 'completed' && (
                    <button 
                      onClick={() => handleCancelBooking(booking._id)}
                      className="px-4 py-2 bg-error/10 text-error rounded-md hover:bg-error/20 transition-colors"
                    >
                      Cancel
                    </button>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Bookings;
