import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { useBookings } from '../context/BookingContext';

// Format date helper function
const formatDateTime = (dateString) => {
  const date = new Date(dateString);
  return date.toLocaleString('en-US', {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    hour: 'numeric',
    minute: 'numeric',
    hour12: true
  });
};

const BookingDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { 
    bookings, 
    loading, 
    error, 
    fetchBookings, 
    getBookingById, 
    cancelBooking, 
    updateBookingStatus 
  } = useBookings();
  
  const [booking, setBooking] = useState(null);
  
  // Get booking from context or fetch it
  useEffect(() => {
    const loadBooking = async () => {
      let foundBooking = getBookingById(id);
      
      if (!foundBooking) {
        // If booking not in context, fetch all bookings
        await fetchBookings();
        foundBooking = getBookingById(id);
        
        // If still not found, redirect to bookings page
        if (!foundBooking) {
          navigate('/bookings');
          return;
        }
      }
      
      setBooking(foundBooking);
    };
    
    loadBooking();
  }, [id, bookings, getBookingById, fetchBookings, navigate]);
  
  // Handle cancellation
  const handleCancel = async () => {
    if (window.confirm('Are you sure you want to cancel this booking?')) {
      const updatedBooking = await cancelBooking(id);
      if (updatedBooking) {
        setBooking(updatedBooking);
      }
    }
  };
  
  // Handle status updates
  const handleStatusUpdate = async (status) => {
    const updatedBooking = await updateBookingStatus(id, status);
    if (updatedBooking) {
      setBooking(updatedBooking);
    }
  };
  
  // Loading state
  if (loading || !booking) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-4 border-primary border-t-transparent"></div>
          <p className="mt-2 text-gray">Loading booking details...</p>
        </div>
      </div>
    );
  }
  
  // Error state
  if (error) {
    return (
      <div className="text-center py-10">
        <div className="text-error text-xl mb-4">
          <i className="fas fa-exclamation-circle mr-2"></i>
          Error loading booking details
        </div>
        <p className="text-gray mb-4">{error}</p>
        <button 
          onClick={() => fetchBookings()}
          className="btn btn-primary"
        >
          Try Again
        </button>
      </div>
    );
  }
  
  // Get status badge class
  const getStatusBadgeClass = (status) => {
    switch (status) {
      case 'pending':
        return 'badge-pending';
      case 'on the way':
        return 'badge-onway';
      case 'arrived':
        return 'badge-arrived';
      case 'completed':
        return 'badge-completed';
      case 'cancelled':
        return 'badge-cancelled';
      default:
        return 'badge-pending';
    }
  };
  
  return (
    <div className="py-10">
      <div className="container mx-auto px-4">
        <div className="max-w-3xl mx-auto">
          <div className="mb-6">
            <Link to="/bookings" className="text-primary flex items-center hover:underline">
              <i className="fas fa-arrow-left mr-2"></i> Back to Bookings
            </Link>
          </div>
          
          <div className="card">
            <div className="mb-6 flex flex-col sm:flex-row sm:items-center justify-between">
              <h1 className="text-2xl font-bold mb-2 sm:mb-0">
                Booking #{booking.bookingId}
              </h1>
              <span className={`badge ${getStatusBadgeClass(booking.status)}`}>
                {booking.status}
              </span>
            </div>
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
              <div>
                <h3 className="text-lg font-semibold mb-4">Ride Details</h3>
                
                <div className="mb-4">
                  <div className="text-sm text-gray mb-1">Pickup Location</div>
                  <div className="font-medium">{booking.pickupLocation}</div>
                </div>
                
                <div className="mb-4">
                  <div className="text-sm text-gray mb-1">Drop Location</div>
                  <div className="font-medium">{booking.dropLocation}</div>
                </div>
                
                <div className="mb-4">
                  <div className="text-sm text-gray mb-1">Date & Time</div>
                  <div className="font-medium">
                    {new Date(booking.date).toLocaleDateString()} at {booking.time}
                  </div>
                </div>
                
                <div className="mb-4">
                  <div className="text-sm text-gray mb-1">Cab Type</div>
                  <div className="font-medium capitalize">{booking.cabType}</div>
                </div>
              </div>
              
              <div>
                <h3 className="text-lg font-semibold mb-4">Booking Information</h3>
                
                <div className="mb-4">
                  <div className="text-sm text-gray mb-1">Booking ID</div>
                  <div className="font-medium">{booking.bookingId}</div>
                </div>
                
                <div className="mb-4">
                  <div className="text-sm text-gray mb-1">Booked On</div>
                  <div className="font-medium">{formatDateTime(booking.createdAt)}</div>
                </div>
                
                <div className="mb-4">
                  <div className="text-sm text-gray mb-1">Fare</div>
                  <div className="font-medium">${booking.fare.toFixed(2)}</div>
                </div>
                
                <div>
                  <div className="text-sm text-gray mb-1">Status</div>
                  <div className="font-medium capitalize">{booking.status}</div>
                </div>
              </div>
            </div>
            
            {/* Status Timeline */}
            <div className="border-t border-b py-6 mb-6">
              <h3 className="text-lg font-semibold mb-4">Ride Status</h3>
              
              <div className="flex items-center mb-6">
                <div className={`h-8 w-8 rounded-full flex items-center justify-center 
                  ${booking.status !== 'cancelled' ? 'bg-primary text-white' : 'bg-gray-200 text-gray'}`}>
                  <i className="fas fa-check text-sm"></i>
                </div>
                <div className={`h-1 flex-1 mx-2 
                  ${(booking.status === 'on the way' || booking.status === 'arrived' || booking.status === 'completed') 
                    ? 'bg-primary' : 'bg-gray-200'}`}></div>
                <div className={`h-8 w-8 rounded-full flex items-center justify-center 
                  ${(booking.status === 'on the way' || booking.status === 'arrived' || booking.status === 'completed') 
                    ? 'bg-primary text-white' : 'bg-gray-200 text-gray'}`}>
                  <i className="fas fa-car text-sm"></i>
                </div>
                <div className={`h-1 flex-1 mx-2 
                  ${(booking.status === 'arrived' || booking.status === 'completed') 
                    ? 'bg-primary' : 'bg-gray-200'}`}></div>
                <div className={`h-8 w-8 rounded-full flex items-center justify-center 
                  ${(booking.status === 'arrived' || booking.status === 'completed') 
                    ? 'bg-primary text-white' : 'bg-gray-200 text-gray'}`}>
                  <i className="fas fa-map-marker-alt text-sm"></i>
                </div>
                <div className={`h-1 flex-1 mx-2 
                  ${booking.status === 'completed' ? 'bg-primary' : 'bg-gray-200'}`}></div>
                <div className={`h-8 w-8 rounded-full flex items-center justify-center 
                  ${booking.status === 'completed' 
                    ? 'bg-primary text-white' : 'bg-gray-200 text-gray'}`}>
                  <i className="fas fa-flag-checkered text-sm"></i>
                </div>
              </div>
              
              <div className="flex justify-between">
                <div className="text-center">
                  <div className="font-medium">Booked</div>
                </div>
                <div className="text-center">
                  <div className="font-medium">On the Way</div>
                </div>
                <div className="text-center">
                  <div className="font-medium">Arrived</div>
                </div>
                <div className="text-center">
                  <div className="font-medium">Completed</div>
                </div>
              </div>
            </div>
            
            {/* Actions */}
            <div className="flex flex-wrap gap-3 justify-end">
              {/* Demo controls for status updates - in a real app this would be driver-controlled */}
              {booking.status !== 'cancelled' && booking.status !== 'completed' && (
                <div className="flex flex-wrap gap-2 mr-auto">
                  <div className="text-sm text-gray mr-2 self-center">Demo Controls:</div>
                  
                  {booking.status === 'pending' && (
                    <button 
                      onClick={() => handleStatusUpdate('on the way')} 
                      className="px-3 py-1 bg-secondary/10 text-secondary text-sm rounded"
                    >
                      Set On the Way
                    </button>
                  )}
                  
                  {booking.status === 'on the way' && (
                    <button 
                      onClick={() => handleStatusUpdate('arrived')} 
                      className="px-3 py-1 bg-secondary/10 text-secondary text-sm rounded"
                    >
                      Set Arrived
                    </button>
                  )}
                  
                  {booking.status === 'arrived' && (
                    <button 
                      onClick={() => handleStatusUpdate('completed')} 
                      className="px-3 py-1 bg-success/10 text-success text-sm rounded"
                    >
                      Complete Ride
                    </button>
                  )}
                </div>
              )}
              
              <Link 
                to="/bookings" 
                className="px-4 py-2 bg-gray-100 text-dark rounded-md hover:bg-gray-200 transition-colors"
              >
                Back
              </Link>
              
              {booking.status !== 'cancelled' && booking.status !== 'completed' && (
                <button 
                  onClick={handleCancel}
                  className="px-4 py-2 bg-error/10 text-error rounded-md hover:bg-error/20 transition-colors"
                >
                  Cancel Booking
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BookingDetails;
